mapper.select:
SELECT
    SUM(CASE WHEN a.order_choice = 'Yes' THEN a.order_amount ELSE 0 END) AS Yes
FROM (
         SELECT *
         FROM tbl_betting_order
         WHERE order_user_no = #{orderUserNo}
           AND order_subject_no = #{orderSubjectNo}
     ) a
GROUP BY a.order_user_no

SELECT
    SUM(CASE WHEN a.order_choice = 'No' THEN a.order_amount ELSE 0 END) AS No
FROM (
         SELECT *
         FROM tbl_betting_order
         WHERE order_user_no = #{orderUserNo}
           AND order_subject_no = #{orderSubjectNo}
     ) a
GROUP BY a.order_user_no
