mapper.update:
UPDATE TBL_USER
SET user_withdrawal_status = #{withdrawalStatus}
WHERE user_no = #{id}

UPDATE tbl_user u
SET u.user_point = u.user_point - #{orderAmount}
WHERE u.user_no = #{orderUserNo} AND u.user_point >= #{orderAmount};

UPDATE  tbl_user
SET user_point=user_point+#{orderAmount}
WHERE user_no=#{orderUserNo}
